class Solution:
    def wordBreak(self,s,dict):
        if dict == {}:
            return False
        f = [True]
        for i in range(1,len(s)+1):
            for j in range(i):
                if self.single_tryout(s[j:i:1],dict) and f[j]:
                    f.append(True)
                    break
            else:
                f.append(False)
        return f
    
    def single_tryout(self,s,dict):
        if s in dict:
            return True
        else:
            return False

                
            
                


